mapper.select:
SELECT c.TESTCASE_NAME as testcaseName, d.DETAIL_ID as detailId, d.TESTCASE_NO as testcaseNo, d.STEP_NO as stepNo, d.MODULE_ID as moduleId, m.MODULE_NAME_CN as moduleNameCn, m.MODULE_NAME as moduleName, d.ELEMENT_DYNAMIC_TYPE as elementType, d.ASSERT_ID as assertId, d.ELEMENT_ID as elementId, d.ELEMENT_DATA as elementData, d.EXE_TIMES as exeTimes, d.DELETED as deleted, d.CREATE_TIME as createTime, d.MODIFY_TIME as modifyTime, d.LAST_OPR as lastOpr, a.LABEL as assertLabel, e.ELEMENT_NAME as elementName FROM AUTO_CASE_DETAIL d LEFT JOIN AUTO_CASE c ON d.TESTCASE_NO = c.TESTCASE_NO LEFT JOIN AUTO_MODULE m ON m.MODULE_ID = d.MODULE_ID LEFT JOIN AUTO_ASSERT a ON d.ASSERT_ID = a.ASSERT_ID LEFT JOIN AUTO_ELEMENT e ON e.ELEMENT_ID = d.ELEMENT_ID
AND c.TESTCASE_NAME like concat('%', #{search}, '%')
AND d.DELETED = #{valid}

SELECT c.TESTCASE_NAME as testcaseName, d.DETAIL_ID as detailId, d.TESTCASE_NO as testcaseNo, d.STEP_NO as stepNo, d.MODULE_ID as moduleId, m.MODULE_NAME_CN as moduleNameCn, m.MODULE_NAME as moduleName, d.ELEMENT_DYNAMIC_TYPE as elementType, d.ASSERT_ID as assertId, d.ELEMENT_ID as elementId, d.ELEMENT_DATA as elementData, d.EXE_TIMES as exeTimes, d.DELETED as deleted, d.CREATE_TIME as createTime, d.MODIFY_TIME as modifyTime, d.LAST_OPR as lastOpr, a.LABEL as assertLabel, e.ELEMENT_NAME as elementName FROM AUTO_CASE_DETAIL d LEFT JOIN AUTO_CASE c ON d.TESTCASE_NO = c.TESTCASE_NO LEFT JOIN AUTO_MODULE m ON m.MODULE_ID = d.MODULE_ID LEFT JOIN AUTO_ASSERT a ON d.ASSERT_ID = a.ASSERT_ID LEFT JOIN AUTO_ELEMENT e ON e.ELEMENT_ID = d.ELEMENT_ID WHERE d.DELETED = '0'
AND c.TESTCASE_NO = #{testcaseNo}
ORDER BY cast(d.STEP_NO as unsigned integer)

SELECT

FROM
    AUTO_CASE_DETAIL
WHERE DELETED = '0'

        AND
            TESTCASE_NO = #{testcaseNo}


        AND
            STEP_NO = #{stepNo}


        AND
            DETAIL_ID != #{detailId}
