mapper.select:
SELECT COUNT(1)
FROM plan_list
WHERE 1=1

    AND work_no = #{workNo}


    AND
    plan_name = #{planName}

mapper.update:
UPDATE
    plan_list
SET
    work_no = #{workNo}
WHERE
    plan_id = #{planId}

UPDATE
    plan_list
SET
    plan_name = #{planName}
WHERE
    plan_id = #{planId}

mapper.select:
