mapper.select:
SELECT distinct f.id, f.task_no, f.test_reason, f.repair_desc, f.jira_no, f.create_time, f.REGRESSION_TEST_SCOPE, f.INTERFACE_CHANGE, f.DATABASE_CHANGE, f.OTHER_SYSTEM_CHANGE, f.DEVELOPER, f.TEST_ENV, f.APP_NAME, f.CLIENT_VERSION, f.WORK_NO, f.RQR_NO, f.group_id, w.main_task_name, w.product_date, w.uat_date, w.testers, w.fdev_group_id, w.work_stage, w.fdev_new, w.order_type FROM ( SELECT * FROM FTMS_SUBMIT_SIT_RECORD
AND LEFT(FTMS_SUBMIT_SIT_RECORD.CREATE_TIME, 10) between #{startDate} and #{endDate}
and group_id in
#{group}
) f JOIN ( SELECT * FROM work_order WHERE work_stage != 11
And work_no IN ( SELECT wt.work_no FROM ( SELECT work_no FROM work_order WHERE main_task_name like concat('%', #{workNo}, '%') OR main_task_no like concat('%', #{workNo}, '%') OR demand_name like concat('%', #{workNo}, '%') OR work_unit like concat('%', #{workNo}, '%') OR demand_no like concat('%', #{workNo}, '%') UNION SELECT work_no FROM task_list WHERE task_name like concat('%', #{workNo}, '%') ) wt )
AND FIND_IN_SET(#{tester},testers)
AND work_stage = #{stage}
AND order_type = #{orderType}
AND order_type = 'function'
and fdev_group_id in
#{group}
) w ON f.work_no = w.work_no ORDER BY f.ID DESC LIMIT #{startPage},#{pageSize}

SELECT count(distinct f.id) FROM ( SELECT * FROM FTMS_SUBMIT_SIT_RECORD
AND LEFT(FTMS_SUBMIT_SIT_RECORD.CREATE_TIME, 10) between #{startDate} and #{endDate}
and group_id in
#{group}
) f JOIN ( SELECT * FROM work_order WHERE work_stage != 11
And work_no IN ( SELECT wt.work_no FROM ( SELECT work_no FROM work_order WHERE main_task_name like concat('%', #{workNo}, '%') OR main_task_no like concat('%', #{workNo}, '%') OR demand_name like concat('%', #{workNo}, '%') OR work_unit like concat('%', #{workNo}, '%') OR demand_no like concat('%', #{workNo}, '%') UNION SELECT work_no FROM task_list WHERE task_name like concat('%', #{workNo}, '%') ) wt )
AND FIND_IN_SET(#{tester},testers)
AND work_stage = #{stage}
AND order_type = #{orderType}
AND order_type = 'function'
and fdev_group_id in
#{group}
) w ON f.work_no = w.work_no

SELECT
FROM FTMS_SUBMIT_SIT_RECORD WHERE WORK_NO = #{workNo}
AND TASK_NO = #{taskNo}
ORDER BY create_time desc LIMIT 0,1

SELECT f.id, f.task_no, f.test_reason, f.repair_desc, f.jira_no, f.create_time, f.REGRESSION_TEST_SCOPE, f.INTERFACE_CHANGE, f.DATABASE_CHANGE, f.OTHER_SYSTEM_CHANGE, f.DEVELOPER, f.TEST_ENV, f.APP_NAME, f.CLIENT_VERSION, f.WORK_NO, f.RQR_NO, f.CORRELATION_SYSTEM, f.CORRELATION_INTERFACE, f.INTERFACE_FILE_PATH, f.TRANS_FILE_PATH FROM FTMS_SUBMIT_SIT_RECORD f left join work_order w on f.work_no=w.work_no WHERE f.task_no = #{taskNo}
AND w.order_type = #{orderType}
AND w.order_type = 'function'
ORDER BY create_time desc

mapper.update:
UPDATE
    FTMS_SUBMIT_SIT_RECORD
SET
    WORK_NO = #{workNo}
WHERE
    id = #{id}

UPDATE
    FTMS_SUBMIT_SIT_RECORD
SET
    GROUP_ID = #{groupId}
WHERE
    TASK_NO = #{taskNo}

UPDATE
    FTMS_SUBMIT_SIT_RECORD
SET
    rqr_no = #{rqrNo}
WHERE
    work_no = #{workOrderNo}
