SELECT e.work_no, e.main_task_name, e.work_stage, e.testers, e.fdev_group_id as group_id, e.order_type, sum(if(ptr.result_status = '1', 1, 0)) as casePass, sum(if(ptr.RESULT_STATUS = '0', 1, 0)) as caseNoExecute, count(ptr.id) as caseCount, IFNULL(sum(ptr.FAIL_EXE_NUM),0) as caseFailure, IFNULL(sum(ptr.BLOCK_EXE_NUM),0) as caseBlock, exe.exe_num as caseExecute, IFNULL(ROUND(sum(if(ptr.result_status = '1', 1, 0)) / count(ptr.id) * 100, 1),0) as percentage FROM ( select distinct w.work_no, w.main_task_name, w.work_stage, w.testers, w.fdev_group_id, w.order_type from ( select w.work_no, w.main_task_name, w.work_stage, w.testers, w.fdev_group_id, w.order_type from work_order w,FTMS_PLAN_RESULT t where w.work_no = t.work_no
AND w.order_type = #{orderType}
AND w.order_type = 'function'
and left(t.CREATE_TM,10) between #{startTime} and #{endTime} group by w.work_no, w.main_task_name, w.work_stage, w.fdev_group_id, w.order_type union all select w.work_no, w.main_task_name, w.work_stage, w.testers, w.fdev_group_id, w.order_type from work_order w,FTMS_TESTCASE_EXE_RECORD e where w.work_no = e.work_no
AND w.order_type = #{orderType}
AND w.order_type = 'function'
and left(e.date,10) between #{startTime} and #{endTime} group by w.work_no, w.main_task_name, w.work_stage, w.fdev_group_id, w.order_type union all select w.work_no, w.main_task_name, w.work_stage, w.testers, w.fdev_group_id, w.order_type from work_order w where from_unixtime(w.uat_submit_date,'%Y-%m-%d') between #{startTime} and #{endTime}
AND w.order_type = #{orderType}
AND w.order_type = 'function'
group by w.work_no, w.main_task_name, w.work_stage, w.fdev_group_id, w.order_type )w )e left join FTMS_PLAN_RESULT ptr on e.work_no = ptr.work_no left join ( select count(e.id) as exe_num,w.work_no from ( select distinct w.work_no,w.main_task_name,w.testers, w.work_stage from ( select w.work_no,w.main_task_name,w.testers, w.work_stage from work_order w,FTMS_PLAN_RESULT t where w.work_no = t.work_no
AND w.order_type = #{orderType}
AND w.order_type = 'function'
and left(t.CREATE_TM,10) between #{startTime} and #{endTime} group by w.work_no,w.main_task_name,w.work_stage union all select w.work_no,w.main_task_name,w.testers,w.work_stage from work_order w,FTMS_TESTCASE_EXE_RECORD e where w.work_no = e.work_no
AND order_type = #{orderType}
AND order_type = 'function'
and left(e.date,10) between #{startTime} and #{endTime} group by w.work_no,w.main_task_name,w.work_stage union all select w.work_no,w.main_task_name,w.testers,w.work_stage from work_order w where from_unixtime(w.uat_submit_date,'%Y-%m-%d') between #{startTime} and #{endTime}
AND order_type = #{orderType}
AND order_type = 'function'
group by w.work_no,w.main_task_name,w.work_stage )w )w left join FTMS_TESTCASE_EXE_RECORD e on e.WORK_NO = w.work_no and e.OPR_TYPE = '0' and e.status != '4' group by w.work_no ) exe on exe.work_no = e.WORK_NO
e.work_stage !="11"
and e.fdev_group_id in
#{item}
AND e.testers like concat('%',#{tester},'%')
AND e.work_stage = #{stage}
AND ( e.main_task_name like concat('%',#{orderNameOrNo},'%') OR e.work_no like concat('%',#{orderNameOrNo},'%') )
group by e.work_no, e.main_task_name, e.work_stage, exe.exe_num

SELECT work_stage,count(1) as orderNum FROM (select e.work_no, e.work_stage, e.fdev_group_id from (select w.work_no, w.work_stage, w.fdev_group_id from work_order w,FTMS_PLAN_RESULT t where w.work_no = t.work_no and left(t.CREATE_TM,10) between #{startTime} and #{endTime}
and w.fdev_group_id in
#{item}
group by w.work_no, w.work_stage, w.fdev_group_id union all select w.work_no, w.work_stage, w.fdev_group_id from work_order w,FTMS_TESTCASE_EXE_RECORD e where w.work_no = e.work_no and left(e.date,10) between #{startTime} and #{endTime}
and w.fdev_group_id in
#{item}
group by w.work_no, w.work_stage, w.fdev_group_id union all select w.work_no, w.work_stage, w.fdev_group_id from work_order w where from_unixtime(w.uat_submit_date,'%Y-%m-%d') between #{startTime} and #{endTime}
and w.fdev_group_id in
#{item}
group by w.work_no, w.work_stage, w.fdev_group_id )e group by e.work_no )work WHERE work.work_stage != '11' GROUP BY work_stage ORDER by CAST(work_stage as SIGNED) DESC;

SELECT count(distinct w.work_no) FROM ( SELECT work_no, fdev_group_id FROM work_order WHERE fdev_group_id is not null AND fdev_group_id = #{groupId} ) w JOIN ( SELECT distinct WORK_NO FROM FTMS_SUBMIT_SIT_RECORD WHERE WORK_NO is not null
and left(CREATE_TIME, 10) >= #{startDate}
and left(CREATE_TIME, 10) <= #{endDate}
GROUP BY WORK_NO ) s ON w.work_no = s.WORK_NO

SELECT orderNum, (sumPayExe + sumOtherExe) as sumExe FROM ( SELECT IFNULL(COUNT(DISTINCT a.work_no), 0) AS orderNum, IFNULL(SUM(IF(e.OPR_TYPE = '0' and a.group_id = '9', 2, 0)), 0) AS sumPayExe, IFNULL(SUM(IF(e.OPR_TYPE = '0' and (a.group_id != '9' or a.group_id is null), 1, 0)), 0) AS sumOtherExe FROM ( SELECT DISTINCT work_no, group_id FROM work_order WHERE order_type = 'function' and from_unixtime(uat_submit_date,'%Y-%m-%d') between #{startDate} and #{endDate}
and fdev_group_id in
#{group}
UNION SELECT DISTINCT w.work_no, w.group_id FROM work_order w JOIN FTMS_PLAN_RESULT r ON w.work_no = r.WORK_NO WHERE w.order_type = 'function' and LEFT(r.CREATE_TM, 10) BETWEEN #{startDate} AND #{endDate}
and w.fdev_group_id in
#{group}
UNION SELECT DISTINCT w.work_no, w.group_id FROM work_order w JOIN FTMS_TESTCASE_EXE_RECORD e ON w.work_no = e.WORK_NO WHERE w.order_type = 'function' and LEFT(e.DATE, 10) BETWEEN #{startDate} AND #{endDate}
and w.fdev_group_id in
#{group}
) a LEFT JOIN (SELECT * FROM FTMS_TESTCASE_EXE_RECORD WHERE LEFT(DATE, 10) BETWEEN #{startDate} AND #{endDate}) e ON a.work_no = e.WORK_NO ) b

SELECT IFNULL(count(work_no),0) as number
FROM work_order
WHERE
    from_unixtime(create_time,'%Y-%m-%d') between #{startDate} and #{endDate}

    AND
    fdev_group_id = #{groupId}

SELECT IFNULL(count(work_no),0) as number FROM ( SELECT w.work_no,w.group_id FROM work_order w,FTMS_PLAN_RESULT t WHERE w.work_no = t.work_no AND left(t.CREATE_TM,10) between #{startDate} and #{endDate}
AND w.fdev_group_id = #{groupId}
UNION SELECT w.work_no,w.group_id FROM work_order w,FTMS_TESTCASE_EXE_RECORD e WHERE w.work_no = e.work_no AND left(e.date,10) between #{startDate} and #{endDate}
AND w.fdev_group_id = #{groupId}
) u

SELECT kk.fdev_group_id as fdevGroupId, ifnull(k.timely, 0) as timely, kk.total as total FROM ( SELECT r.fdev_group_id, count(r.work_no) as timely FROM ( SELECT w.work_no, w.sit_date, w.fdev_group_id, s.CREATE_TIME FROM ( SELECT work_no, fdev_group_id, sit_date FROM work_order WHERE fdev_group_id is not null AND sit_date is not null
and left(from_unixtime(create_time), 10) >= #{startDate}
and left(from_unixtime(create_time), 10) <= #{endDate}
AND fdev_group_id in (${fdevGroupId}) ) w JOIN ( SELECT work_no, min(CREATE_TIME) as CREATE_TIME FROM FTMS_SUBMIT_SIT_RECORD WHERE CREATE_TIME is not null AND WORK_NO is not null GROUP BY work_no ) s ON w.work_no = s.WORK_NO AND replace(w.sit_date, '/', '-') >= left(s.CREATE_TIME, 10) ) r group by r.fdev_group_id ) k RIGHT JOIN ( SELECT r.fdev_group_id, count(r.work_no) as total FROM ( select w.work_no, w.sit_date, w.fdev_group_id, s.CREATE_TIME from ( SELECT work_no, fdev_group_id, sit_date FROM work_order WHERE fdev_group_id is not null AND sit_date is not null AND fdev_group_id in (${fdevGroupId})
and left(from_unixtime(create_time), 10) >= #{startDate}
and left(from_unixtime(create_time), 10) <= #{endDate}
) w JOIN ( SELECT work_no, min(CREATE_TIME) as CREATE_TIME FROM FTMS_SUBMIT_SIT_RECORD WHERE CREATE_TIME is not null AND WORK_NO is not null GROUP BY work_no ) s ON w.work_no = s.WORK_NO ) r GROUP BY r.fdev_group_id ) kk ON k.fdev_group_id = kk.fdev_group_id

SELECT b.fdev_group_id as fdevGroupId, ifnull(a.sucNum, 0) as timely, ifnull(b.total, 0) as total FROM ( SELECT w.fdev_group_id, count(distinct w.work_no) as sucNum FROM ( SELECT work_no, fdev_group_id FROM work_order WHERE fdev_group_id is not null AND fdev_group_id in (${fdevGroupId}) ) w JOIN ( SELECT distinct WORK_NO FROM FTMS_SUBMIT_SIT_RECORD WHERE WORK_NO is not null
and left(CREATE_TIME, 10) >= #{startDate}
and left(CREATE_TIME, 10) <= #{endDate}
GROUP BY WORK_NO ) s ON w.work_no = s.WORK_NO WHERE w.work_no not in (SELECT distinct WORK_NO FROM FTMS_ROLLBACK_INFO ) GROUP BY w.fdev_group_id ) a LEFT JOIN ( SELECT w.fdev_group_id, count(distinct w.work_no) as total FROM ( SELECT work_no, fdev_group_id FROM work_order WHERE fdev_group_id is not null AND fdev_group_id in (${fdevGroupId}) ) w JOIN ( SELECT distinct WORK_NO FROM FTMS_SUBMIT_SIT_RECORD WHERE WORK_NO is not null
and left(CREATE_TIME, 10) >= #{startDate}
and left(CREATE_TIME, 10) <= #{endDate}
GROUP BY WORK_NO ) s ON w.work_no = s.WORK_NO GROUP BY w.fdev_group_id ) b ON a.fdev_group_id = b.fdev_group_id

SELECT w.fdev_group_id as fdevGroupId, count(e.id) as exeTime FROM work_order w JOIN FTMS_TESTCASE_EXE_RECORD e ON w.work_no = e.WORK_NO WHERE e.OPR_TYPE = '0' AND w.fdev_group_id is not null AND w.fdev_group_id in (${fdevGroupId})
and left(DATE, 10) >= #{startDate}
and left(DATE, 10) <= #{endDate}
GROUP BY w.fdev_group_id
