mapper.select:
SELECT distinct work_no work_no, main_task_no main_task_no, main_task_name main_task_name, work_stage work_stage, work_unit work_unit, testers testers, sit_date sit_date, product_date product_date, uat_date uat_date, work_flag work_flag, work_manager work_manager, work_leader work_leader, remark remark, create_time create_time, field1 field1,field2 field2,field3 field3,group_id group_id,field5 field5, uat_submit_date uat_submit_date, risk_description risk_description, sit_flag sit_flag, approval_flag approval_flag, fdev_group_id fdev_group_id, fst_sit_date fst_sit_date, fnl_sit_date fnl_sit_date, group_id group_id, image_link image_link, demand_no demand_no, demand_name demand_name, order_type FROM work_order w WHERE work_flag = '1' AND (work_manager IS NULL OR TRIM(work_manager)='') AND work_stage = '0'
And work_no IN ( SELECT wt.work_no FROM ( SELECT work_no FROM work_order WHERE main_task_name like #{taskName} OR main_task_no like #{taskName} OR demand_name like #{taskName} OR work_unit like #{taskName} OR demand_no like #{taskName} UNION SELECT work_no FROM task_list WHERE task_name like #{taskName} ) wt )
ORDER BY create_time desc LIMIT #{start},#{pageSize}

