mapper.select:
SELECT group_concat(distinct work_no) as work_no, main_task_no main_task_no, main_task_name main_task_name, work_stage work_stage, work_unit work_unit, testers testers, sit_date sit_date, product_date product_date, uat_date uat_date, work_flag work_flag, work_manager work_manager, work_leader work_leader, remark remark, create_time create_time, field1 field1,field2 field2,field3 field3,group_id group_id,field5 field5, uat_submit_date uat_submit_date, risk_description risk_description, sit_flag sit_flag, approval_flag approval_flag, fdev_group_id fdev_group_id, fst_sit_date fst_sit_date, fnl_sit_date fnl_sit_date, image_link image_link, demand_no demand_no, demand_name demand_name, order_type as orderType FROM work_order
And work_no IN ( SELECT wt.work_no FROM ( SELECT work_no FROM work_order WHERE main_task_name like #{taskName} OR main_task_no like #{taskName} OR demand_name like #{taskName} OR work_unit like #{taskName} OR demand_no like #{taskName} UNION SELECT work_no FROM task_list WHERE task_name like #{taskName} ) wt )
AND ( FIND_IN_SET(#{userEnName},testers) OR FIND_IN_SET(#{userEnName},work_leader) OR work_manager = #{userEnName})
AND fdev_group_id = #{groupId}
AND order_type = #{orderType}
AND order_type = 'function'
GROUP BY work_no order by create_time desc limit #{start},#{pageSize}

SELECT count(distinct work_no) FROM work_order
And work_no IN ( SELECT wt.work_no FROM ( SELECT work_no FROM work_order WHERE main_task_name like #{taskName} OR main_task_no like #{taskName} OR demand_name like #{taskName} OR work_unit like #{taskName} OR demand_no like #{taskName} UNION SELECT work_no FROM task_list WHERE task_name like #{taskName} ) wt )
AND ( FIND_IN_SET(#{userEnName},testers) OR FIND_IN_SET(#{userEnName},work_leader) OR work_manager = #{userEnName})
AND fdev_group_id = #{groupId}
AND order_type = #{orderType}
AND order_type = 'function'

SELECT distinct w.work_no work_no, w.main_task_no main_task_no, w.main_task_name main_task_name, w.work_stage work_stage, w.work_unit work_unit, w.testers testers, w.sit_date sit_date, w.product_date product_date, w.uat_date uat_date, w.work_flag work_flag, w.work_manager work_manager, w.work_leader work_leader, w.remark remark, w.create_time create_time, w.field1 field1,w.field2 field2,w.field3 field3,w.group_id group_id,w.field5 field5, w.uat_submit_date uat_submit_date, w.risk_description risk_description, w.sit_flag sit_flag, w.approval_flag approval_flag, w.fdev_group_id fdev_group_id, w.fst_sit_date fst_sit_date, w.fnl_sit_date fnl_sit_date, w.group_id group_id, w.image_link image_link, w.demand_no demand_no, w.demand_name demand_name, w.order_type as orderType FROM work_order w WHERE w.work_stage in (6,10,13) AND w.approval_flag = 0
And w.work_no IN ( SELECT wt.work_no FROM ( SELECT work_no FROM work_order WHERE main_task_name like #{taskName} OR main_task_no like #{taskName} OR demand_name like #{taskName} OR work_unit like #{taskName} OR demand_no like #{taskName} UNION SELECT work_no FROM task_list WHERE task_name like #{taskName} ) wt )
AND FIND_IN_SET(#{userEnName}, w.field2)
AND w.order_type = #{orderType}
AND w.order_type = 'function'
LIMIT #{start}, #{pageSize}

SELECT work_no FROM work_order WHERE main_task_no = #{task_id} AND work_stage != '11'
AND order_type = #{orderType}
AND order_type = 'function'

SELECT a.groupId as groupId, a.caseNum as caseNumber, a.exeCaseNum as performCaseNumber, b.orderNum as requestNumber FROM (SELECT w.fdev_group_id as groupId, IFNULL(count(p.id), 0) as caseNum, SUM(IFNULL(EXE_NUM, 0)) as exeCaseNum FROM (SELECT fdev_group_id, work_no FROM work_order
and from_unixtime(uat_submit_date,'%Y/%m/%d') >= #{startDate}
and from_unixtime(uat_submit_date,'%Y/%m/%d') <= #{endDate}
and fdev_group_id in
#{item}
) w LEFT JOIN FTMS_PLAN_RESULT p ON w.work_no = p.work_no GROUP BY w.fdev_group_id )a, (SELECT fdev_group_id, IFNULL(count(work_no), 0) as orderNum From work_order
and from_unixtime(uat_submit_date,'%Y/%m/%d') >= #{startDate}
and from_unixtime(uat_submit_date,'%Y/%m/%d') <= #{endDate}
and fdev_group_id in
#{item}
AND order_type = #{orderType}
AND order_type = 'function'
GROUP BY fdev_group_id )b WHERE a.groupId = b.fdev_group_id

